package com.example.demo.simpletest.socket;import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;import java.io.PrintWriter;import java.net.ServerSocket;import java.net.Socket;import java.util.HashMap;import java.util.Map;/** * ProjectName * <BR><B>FileDescription：</B><BR><BR> * DescriptionDetail * * @author ：ZhangHanYuan zhang.hanyuan@qq.com * @date : 2019-05-23 18:05 * @since 1.8 */public class SocketServer {    public static void main(String[] args) throws IOException {        Map<String,String > test = new HashMap<String,String>();        ServerSocket server=new ServerSocket(5678);        while(true){            Socket client=server.accept();            BufferedReader in=new BufferedReader(new InputStreamReader(client.getInputStream()));            PrintWriter out=new PrintWriter(client.getOutputStream());            while(true){                String str=in.readLine();                System.out.println(str);                out.println("has receive....");                out.flush();                if(str.equals("end"))                    break;            }            client.close();        }    }}